{
  "pydantic_models": [
    {
      "model_name": "AgentConfigSchema",
      "docstring": "Configuration schema for SACM agents, specifying the agent's name, associated model, and location of the instructions file. The instructions_path can be a string or a pathlib.Path.",
      "fields": [
        {
          "name": "agent_name",
          "type": "str",
          "doc": "The unique name assigned to the agent."
        },
        {
          "name": "model_id",
          "type": "str",
          "doc": "The identifier for the model to be used by this agent."
        },
        {
          "name": "instructions_path",
          "type": "Union[str, Path]",
          "doc": "Path to the instructions file, as a string or pathlib.Path object."
        }
      ]
    }
  ],
  "component_interfaces": [],
  "description": "Defines AgentConfigSchema Pydantic model for agent configuration in the SACM system with 'agent_name', 'model_id', and dual-typed 'instructions_path'."
}

